script.script:
import hudson.model.*
import groovy.json.JsonBuilder
import groovy.json.JsonOutput
import groovy.json.JsonSlurper
import com.roadrunner.service.HipchatV1Service

/****  
    THIS SCRIPT CREATES THE JENKINS OUTPUT REPORT
    1.  CALLS ROADRUNNER START API TO IMPORT SOASTA RESULTS INTO ROADRUNNER
    2.  CREATES LINKS TO ROADRUNNER REPORT
    2.  SETUPS UP ROADRUNNER IFRAME WITH JENKINS EXTJS VIEW
    3.  GETS SLA RESULTS
    4.  PASS/FAILS THE JOB
    5.  DISPLAYS ALL JSON RESPONSES 
****/


def jsonSlurper = new JsonSlurper();
StringBuilder hipchatText = new StringBuilder();

// get SOASTA result ID
def EXTERNAL_TEST_ID = getSoastaReportId()

// BOMB here if I don't have an external test ID
if(EXTERNAL_TEST_ID == "NA") {
   // abort test and report to hipchat
  hipchatText.append("<b>Test Name: </b> " + manager.build.buildVariableResolver.resolve("TEST_NAME") + "  ---  " + manager.build.buildVariableResolver.resolve("TEST_SUB_NAME"));
  hipchatText.append("<br /><b>Application(s): </b> " + manager.build.buildVariableResolver.resolve("APP_UNDER_TEST"));
  hipchatText.append("<br /><b>CloudTest error while trying to run test</b>");
  if(!hipchatRoom.trim().equals("")){
     HipchatV1Service hipchat = new HipchatV1Service();
     hipchat.sendMessage(hipchatRoom.trim(), hipchatText.toString(), "red");
  }
  manager.buildFailure()
  throw new Exception("CloudTest failed to execute")
}

// build query params
def params = [
  ctComposition: URLEncoder.encode(manager.build.buildVariableResolver.resolve("CT_COMPOSITION_FULL_PATH").tokenize('/').last()),
  testName: URLEncoder.encode(manager.build.buildVariableResolver.resolve("TEST_NAME")), 
  testSubName: URLEncoder.encode(manager.build.buildVariableResolver.resolve("TEST_SUB_NAME")),    
  appUnderTest: URLEncoder.encode(manager.build.buildVariableResolver.resolve("APP_UNDER_TEST")), 
  appUnderTestVersion: URLEncoder.encode(manager.build.buildVariableResolver.resolve("APP_UNDER_TEST_VERSION")), 
  description: URLEncoder.encode(manager.build.buildVariableResolver.resolve("DESCRIPTION")),
  comments: URLEncoder.encode(manager.build.buildVariableResolver.resolve("COMMENTS")), 
  vuserCount: manager.build.buildVariableResolver.resolve("VUSER_COUNT"),
  environment: URLEncoder.encode(manager.build.buildVariableResolver.resolve("ENVIRONMENT")),
  testTool: URLEncoder.encode(manager.build.buildVariableResolver.resolve("TEST_TOOL")),
  testToolVersion: URLEncoder.encode(manager.build.buildVariableResolver.resolve("TEST_TOOL_VERSION")),
  startTime: Math.round((System.currentTimeMillis()/1000)*100)/100,
  testDataType: manager.build.buildVariableResolver.resolve("TEST_DATA_TYPE"),
  slaGroupId: manager.build.buildVariableResolver.resolve("SLA_GROUP_ID"),
  externalTestId: EXTERNAL_TEST_ID
]

// build query string
def queryString = params.collect { k,v -> "$k=$v" }.join('&')

// set link to API call
def RR_SOASTA_START_TEST_URL = "http://opp-svc.mydomain.com/loadsvc/v1/soastacloudtest/start/?" + queryString
println "--- FULL REQUEST: " + RR_SOASTA_START_TEST_URL
def resp = restCall(RR_SOASTA_START_TEST_URL, "GET", "")

// get links for iframe
def resultsLinkSecure = ""
def resultsLink = ""
def jsonRes

// validate results and set loadTestId
def hadError = false
def loadTestId = 0
def outputMsg = ""
try {
    jsonRes = jsonSlurper.parseText(resp)
    loadTestId = jsonRes.loadtest.id

    // get links for iframe
   resultsLinkSecure = "https://opp-ui.mydomain.com/?view=jenkins&id="+loadTestId
   resultsLink = "http://opp-ui.mydomain.com/?view=jenkins&id="+loadTestId

    def jsonPretty = JsonOutput.prettyPrint(resp).replace(' ', '&nbsp;').replace('\n', '<br />')

    if(jsonPretty != ""){
       outputMsg = "<br />" + jsonPretty
    } else {
       outputMsg = resp
    }
} catch(e){
    outputMsg = "ERROR: " + e.getStackTrace()
    hadError = true
}

// add test name and details to output
hipchatText.append("<b>Test Name: </b> " + manager.build.buildVariableResolver.resolve("TEST_NAME") + "  ---  " + manager.build.buildVariableResolver.resolve("TEST_SUB_NAME"));
hipchatText.append("<br /><b>Application(s): </b> " + manager.build.buildVariableResolver.resolve("APP_UNDER_TEST"));



// set SOASTA results link
def cloudTestResLink = "http://cloudtest.mydomain.com/concerto/Central?initResultsTab=$EXTERNAL_TEST_ID"

// --- GET SLAs ---
def slaResp = restCall('http://opp-svc.mydomain.com/loadsvc/v1/loadtests/'+loadTestId.toString()+'/slaresults', "GET", "")
def slaPassed = true  // default to true.  So if no SLAs are found, it stays as true
def slaJsonPretty = ""
def slaOutputMsg = ""
try {
  def slaJsonRes = jsonSlurper.parseText(slaResp)
  slaPassed = slaJsonRes.passed
  slaJsonPretty = JsonOutput.prettyPrint(slaResp).replace(' ', '&nbsp;').replace('\n', '<br />')    
  slaOutputMsg = "<br />" + slaJsonPretty

 // add hipchat message
  hipchatText.append("<br /><br /><b>SLA Results:</b>: ");
  hipchatText.append("<br />Passed: " + slaJsonRes.passed);
  hipchatText.append("<br />Total Transactions: " + slaJsonRes.total_transactions);
  hipchatText.append("<br />Total SLAs: " + slaJsonRes.total_slas);
  hipchatText.append("<br />Warnings: " + slaJsonRes.warning_count);
  hipchatText.append("<br />Errors: " + slaJsonRes.error_count);

} catch(e){
  slaResult += "Failed to pretty print. $e.message"
  slaHadError = true
}

StringBuilder summaryText = new StringBuilder();
String summaryIcon = "";
String hipchatColor = "green";

if(!hadError){
     // add hipchat messages
     hipchatText.append("<br /><br /><b>RoadRunner Results: </b><a href='"+resultsLink+"' target='_blank'>"+resultsLink+"</a>");
     hipchatText.append("<br /><b>CloudTest Results: </b><a href='"+cloudTestResLink+"' target='_blank'>"+cloudTestResLink+"</a>")
     hipchatText.append("<p>&nbsp;</p>");
} else {
     // add hipchat messages
     hipchatText.append("<br /><br /><b>TEST FAILED AND HAD ERRORS</b>");
     hipchatText.append("<p>&nbsp;</p>");
}

if(!hadError && slaPassed){
  summaryIcon = "green.gif"
  summaryText.append("<script>jQuery('#main-panel > table').css('width','100%');</script>")
  summaryText.append("<p><b>RoadRunner Results: </b><a href='"+resultsLink+"' target='_blank'>"+resultsLink+"</a>")
  summaryText.append("<br /><br /><b>CloudTest Results: </b><a href='"+cloudTestResLink+"' target='_blank'>"+cloudTestResLink+"</a></p>")
  summaryText.append("<p>&nbsp;</p><p>Tip: If the content isn't loading below, load the direct link above with HTTPS and accept the SSL.  Then refresh this page.</p><iframe src='"+resultsLinkSecure+"' height='1200' width='100%'></iframe>")
  summaryText.append("<b>New Test Data:</b> $outputMsg")
  summaryText.append("<br /><br /><b>SOASTA Report Id:</b>: $EXTERNAL_TEST_ID");
  summaryText.append("<br /><br /><b>SLA Results:</b>: " + slaOutputMsg);

} else {
  // either slas failed or test failed
  summaryIcon = "error.gif"
  hipchatColor = "red"

  if(hadError) { 
     // test failed
     summaryText.append("<b>ERROR DETAILS:</b> $outputMsg")
     summaryText.append("<br /><br /><b>API Call:</b> " + RR_SOASTA_START_TEST_URL + "?" + queryString)
     summaryText.append("<br /><br />CloudTest Results: <a href='"+cloudTestResLink+"' target='_blank'>"+cloudTestResLink+"</a>")
     summaryText.append("<br /><br /><b>SOASTA Report Id:</b>: $EXTERNAL_TEST_ID");
     summaryText.append("<br /><br /><b>SLA Results:</b>: " + slaOutputMsg);
  } else {
     // just slas failed
     summaryText.append("<script>jQuery('#main-panel > table').css('width','100%');</script>")
     summaryText.append("<b>ERROR DETAILS:</b> SLAs failed!  <a href='#slas'>See results</a>")
     summaryText.append("<p><b>RoadRunner Results: </b><a href='"+resultsLink+"' target='_blank'>"+resultsLink+"</a>")
     summaryText.append("<br /><br /><b>CloudTest Results: </b><a href='"+cloudTestResLink+"' target='_blank'>"+cloudTestResLink+"</a></p>")
     summaryText.append("<p>&nbsp;</p><p>Tip: If the content isn't loading below, load the direct link above with HTTPS and accept the SSL.  Then refresh this page.</p><iframe src='"+resultsLinkSecure+"' height='1200' width='100%'></iframe>")
     summaryText.append("<b>New Test Data:</b> $outputMsg")
     summaryText.append("<br /><br /><b>SOASTA Report Id:</b>: $EXTERNAL_TEST_ID");
     summaryText.append("<br /><a name='slas' id='slas' /><br /><b>SLA Results:</b>: " + slaOutputMsg);
  }
  manager.buildFailure()
}

summary = manager.createSummary(summaryIcon);
summary.appendText(summaryText.toString(), false);
String hipchatRoom = manager.build.buildVariableResolver.resolve("HIPCHAT_ROOM_NAME");
if(!hipchatRoom.trim().equals("")){
   HipchatV1Service hipchat = new HipchatV1Service();
   hipchat.sendMessage(hipchatRoom.trim(), hipchatText.toString(), hipchatColor);
}



def restCall(def strUrl, def method, def queryString){
  def url = new URL(strUrl)
  def conn = url.openConnection()
  conn.setReadTimeout(10*120*1000) // 20 minutes for RoadRunner to process data
  conn.setRequestMethod(method)
  conn.setRequestProperty("Content-Type", "application/json; charset=utf-8");
  conn.setRequestProperty("Accept","*/*")
  if(method == "POST" || method == "PUT"){
     conn.doOutput = true
     if(queryString.trim() != '') {
       def writer = new OutputStreamWriter(conn.outputStream)
       writer.write(queryString)
       writer.flush()
       writer.close()
    }
  } else {
    conn.doOutput = false
  }

  conn.connect()

  def respText = conn.content.text
  println "====== RESPONSE CODE: " + conn.responseCode
  println "====== RESPONSE TEXT: " + respText

  return respText
}


def getSoastaReportId() {
  println "getting Soasta Report Id..."
  def allNodes
  def soastaId = "0"
  def pattern = ~/.*\.xml/
  def dirname='/var/lib/jenkins/jobs/' + manager.build.getProject().name + '/builds/' + manager.build.getId() + '/archive/';
  def found = false;

  new File("$dirname").eachDirRecurse { dir ->
    if(!found){
        dir.eachFileMatch(pattern) {    myfile ->
          if(!found){
            try {
              allNodes = new XmlSlurper().parseText(new File("$myfile").text).depthFirst().collect{ it }
              for( n in allNodes){
                //println n.@resultID
                if(n.@resultID!=null && !n.@resultID.text().equals("")){
                  soastaId = n.@resultID.text()
                  printlin "found id of " + soastaId
                  found = true
                  break;
                }
              }
           //   soastaId = new XmlSlurper().parseText(new File("$myfile").text).testcase.@resultID.text()Ã¢€‹.toString()

              found = true
            }catch(Exception e){
              // println e.getStackTrace()
            }
          }
        } // eachFileMatch
   } // end if !found
  } // eachFileMatch


  return soastaId
}
