scriptSource.command:
import hudson.model.*
import groovy.json.JsonBuilder
import groovy.json.JsonOutput
import groovy.json.JsonSlurper

/****  
          THIS SCRIPT CREATES A ${ROADRUNNER_TESTID} VARIABLE WHICH 
          IS THEN PASSED INTO THE SOASTA CLOUDTEST ADDITIONAL OPTIONS FIELD 
****/

def thr = Thread.currentThread()
def build = thr?.executable

def SLA_GROUP_ID = build.buildVariableResolver.resolve("SLA_GROUP_ID")
if(SLA_GROUP_ID.trim() == '') SLA_GROUP_ID = "0"


def CREATE_LOADTEST_URL = "http://roadrunner.roving.com/loadsvc/v1/loadtests"

def json = new groovy.json.JsonBuilder(
{ 
  test_name URLEncoder.encode(build.buildVariableResolver.resolve("TEST_NAME"))
  test_sub_name URLEncoder.encode(build.buildVariableResolver.resolve("TEST_SUB_NAME"))
  app_under_test URLEncoder.encode(build.buildVariableResolver.resolve("APP_UNDER_TEST"))
  app_under_test_version URLEncoder.encode(build.buildVariableResolver.resolve("APP_UNDER_TEST_VERSION"))
  description URLEncoder.encode(build.buildVariableResolver.resolve("DESCRIPTION"))
  comments URLEncoder.encode(build.buildVariableResolver.resolve("COMMENTS"))
  vuser_count build.buildVariableResolver.resolve("VUSER_COUNT")
  environment URLEncoder.encode(build.buildVariableResolver.resolve("ENVIRONMENT"))
  test_tool URLEncoder.encode(build.buildVariableResolver.resolve("TEST_TOOL"))
  test_tool_version URLEncoder.encode(build.buildVariableResolver.resolve("TEST_TOOL_VERSION"))
  start_time Math.round((System.currentTimeMillis()/1000)*100)/100
  sla_group_id SLA_GROUP_ID
}).toString()

// make API call
def resp = restCall(CREATE_LOADTEST_URL, "POST", json)

println "====== ATTEMPTING TO PARSE RESULTS ======"
try {
  def jsonSlurper = new JsonSlurper()
  def loadTest = jsonSlurper.parseText(resp)
  println "====== Setting ROADRUNNER_TESTID to: " + loadTest.id
  def pa = new ParametersAction([
    new StringParameterValue("ROADRUNNER_TESTID", loadTest.id.toString()),
    new StringParameterValue("ROADRUNNER_CREATE_TEST_RESP", resp)
  ])
  Thread.currentThread().executable.addAction(pa)
} catch (Exception e){
  println "====== ERROR PARSING JSON RESPONSE ======"
  println e2 
}


def restCall(def strUrl, def method, def queryString){
  def url = new URL(strUrl)
  def conn = url.openConnection()
  conn.setReadTimeout(5*60*1000) // 5 minutes for RoadRunner to process data
  conn.setRequestMethod(method)
  conn.setRequestProperty("Content-Type", "application/json; charset=utf-8");
  conn.setRequestProperty("Accept","*/*")
  if(method == "POST" || method == "PUT"){
     conn.doOutput = true
     if(queryString.trim() != '') {
       def writer = new OutputStreamWriter(conn.outputStream)
       writer.write(queryString)
       writer.flush()
       writer.close()
    }
  } else {
    conn.doOutput = false
  }

  conn.connect()

  def respText = conn.content.text
  println "====== RESPONSE CODE: " + conn.responseCode
  println "====== RESPONSE TEXT: " + respText

  return respText
}

script.script:
import hudson.model.*
import groovy.json.JsonBuilder
import groovy.json.JsonOutput
import groovy.json.JsonSlurper

/****  
    THIS SCRIPT BUILDS OUT JENKINS OUTPUT REPORT.
    1.  CREATES LINKS TO ROADRUNNER REPORT
    2.  SETUPS UP ROADRUNNER IFRAME WITH JENKINS EXTJS VIEW
    3.  GETS SLA RESULTS
    4.  PASS/FAILS THE JOB
    5.  DISPLAYS ALL JSON RESPONSES 
****/


def jsonSlurper = new JsonSlurper()

// get SOASTA result ID
def EXTERNAL_TEST_ID = getSoastaReportId()

// BOMB here if I don't have an external test ID
if(EXTERNAL_TEST_ID == "NA") {
  manager.buildFailure()
  throw new Exception("CloudTest failed to execute")
}

// get test id
def loadTestId = manager.build.getEnvironment(manager.listener)['ROADRUNNER_TESTID']

// get initial json response for test id
def rrCreateResp = manager.build.getEnvironment(manager.listener)['ROADRUNNER_CREATE_TEST_RESP']
// pretty print initial load test json
def rrCreateRespPretty = ""
try {
   rrCreateRespPretty = JsonOutput.prettyPrint(rrCreateResp).replace(' ', '&nbsp;').replace('\n', '<br />')
} catch (e){
   rrCreateRespPretty = "error retrieving data"
}

// set SOASTA results link
def cloudTestResLink = "http://cloudtest.roving.com/concerto/Central?initResultsTab=$EXTERNAL_TEST_ID"

// --- GET SLAs ---
def slaResp = restCall('http://roadrunner.roving.com/loadsvc/v1/loadtests/'+loadTestId.toString()+'/slaresults', "GET", "")
def slaPassed = true  // default to true.  So if no SLAs are found, it stays as true
def slaJsonPretty = ""
def slaOutputMsg = ""
try {
  def slaJsonRes = jsonSlurper.parseText(slaResp)
  slaPassed = slaJsonRes.passed
  slaJsonPretty = JsonOutput.prettyPrint(slaResp).replace(' ', '&nbsp;').replace('\n', '<br />')    
  slaOutputMsg = "<br />" + slaJsonPretty

} catch(e){
  slaResult += "Failed to pretty print. $e.message"
  slaHadError = true
}


// ---aggregate data ---
def aggResp = restCall('http://roadrunner.roving.com/loadsvc/v1/loadtests/'+loadTestId.toString()+'/data/aggregate', "POST", "")
def aggRespPretty = ""
try {
  aggRespPretty = JsonOutput.prettyPrint(aggResp).replace(' ', '&nbsp;').replace('\n', '<br />')    
 } catch(e){
  aggRespPretty = "Failed to aggregate load test data"
}


// get links for iframe
def resultsLinkSecure = "https://roadrunner.roving.com/?view=jenkins&id="+loadTestId
def resultsLink = "http://roadrunner.roving.com/?view=jenkins&id="+loadTestId

if(slaPassed){
  summary = manager.createSummary("green.gif")
  summary.appendText("<script>jQuery('#main-panel > table').css('width','100%');</script>", false)
  summary.appendText("<p><b>RoadRunner Results: </b><a href='"+resultsLink+"' target='_blank'>"+resultsLink+"</a>", false)
  summary.appendText("<br /><br /><b>CloudTest Results: </b><a href='"+cloudTestResLink+"' target='_blank'>"+cloudTestResLink+"</a></p>", false)
  summary.appendText("<p>&nbsp;</p><p>Tip: If the content isn't loading below, load the direct link above with HTTPS and accept the SSL.  Then refresh this page.</p><iframe src='"+resultsLinkSecure+"' height='1200' width='100%'></iframe>", false)
  summary.appendText("<b>New Test Data:</b> $rrCreateRespPretty", false)
  summary.appendText("<br /><br /><b>SOASTA Report Id:</b>: $EXTERNAL_TEST_ID", false);
  summary.appendText("<br /><br /><b>SLA Results:</b>: " + slaOutputMsg, false);
  summary.appendText("<br /><br /><b>Data Aggregation Results:</b>: " + aggRespPretty, false);
} else {
  // either slas failed or test failed
  summary = manager.createSummary("error.gif")

  summary.appendText("<script>jQuery('#main-panel > table').css('width','100%');</script>", false)
  summary.appendText("<b>ERROR DETAILS:</b> SLAs failed!  <a href='#slas'>See results</a>", false)
  summary.appendText("<p><b>RoadRunner Results: </b><a href='"+resultsLink+"' target='_blank'>"+resultsLink+"</a>", false)
  summary.appendText("<br /><br /><b>CloudTest Results: </b><a href='"+cloudTestResLink+"' target='_blank'>"+cloudTestResLink+"</a></p>", false)
  summary.appendText("<p>&nbsp;</p><p>Tip: If the content isn't loading below, load the direct link above with HTTPS and accept the SSL.  Then refresh this page.</p><iframe src='"+resultsLinkSecure+"' height='1200' width='100%'></iframe>", false)
  summary.appendText("<b>New Test Data:</b> $rrCreateRespPretty", false)
  summary.appendText("<br /><br /><b>SOASTA Report Id:</b>: $EXTERNAL_TEST_ID", false);
  summary.appendText("<br /><a name='slas' id='slas' /><br /><b>SLA Results:</b>: " + slaOutputMsg, false);
summary.appendText("<br /><br /><b>Data Aggregation Results:</b>: " + aggRespPretty, false);

  manager.buildFailure()
}


