  def conn = url.openConnection()
  conn.setRequestMethod(method)
  conn.setRequestProperty("Content-Type", "application/json; charset=utf-8");
  conn.setRequestProperty("Accept","*/*")
  if(method == "POST" || method == "PUT"){
     conn.doOutput = true
     if(queryString.trim() != '') {
       def writer = new OutputStreamWriter(conn.outputStream)
       writer.write(queryString)
       writer.flush()
       writer.close()
    }
  } else {
    conn.doOutput = false
  }

  conn.connect()

  def respText = conn.content.text
  println "====== RESPONSE CODE: " + conn.responseCode
  println "====== RESPONSE TEXT: " + respText

  return respText
}

def getSoastaReportId() {
  println "getting Soasta Report Id..."
  def allNodes
  def soastaId = "0"
  def pattern = ~/.*\.xml/
  def dirname='/var/lib/jenkins/jobs/' + manager.build.getProject().name + '/builds/' + manager.build.getId() + '/archive/';
  def found = false;

  new File("$dirname").eachDirRecurse { dir ->
    if(!found){
        dir.eachFileMatch(pattern) {    myfile ->
          if(!found){
            try {
              allNodes = new XmlSlurper().parseText(new File("$myfile").text).depthFirst().collect{ it }
              for( n in allNodes){
                //println n.@resultID
                if(n.@resultID!=null && !n.@resultID.text().equals("")){
                  soastaId = n.@resultID.text()
                  printlin "found id of " + soastaId
                  found = true
                  break;
                }
              }
           //   soastaId = new XmlSlurper().parseText(new File("$myfile").text).testcase.@resultID.text()â€‹.toString()

              found = true
            }catch(Exception e){
              // println e.getStackTrace()
            }
          }
        } // eachFileMatch
   } // end if !found
  } // eachFileMatch


  return soastaId
}
